import React, { useState } from "react";
import { TextInput, StyleSheet, View, Text, SafeAreaView } from 'react-native'

const Register = () => {
    const [ced, setCed] = useState('');
    return (
        <SafeAreaView style={styles.safeArea}>
            <View style={styles.container}>
                <View>
                    <Text style={styles.text}>REGISTRO SÍNTOMAS</Text>
                </View>
                <View>
                    <Text style={styles.text}>Datos paciente:</Text>
                    <TextInput style={styles.password}
                            placeholder="Cédula"
                            value={ced}
                            onChangeText={(text) => setCed(text)} />
                    <Text style={styles.textInf}>Es una enfermedad viral común en niños pequeños. Se caracteriza por fiebre alta seguida de una erupción rosada en el cuerpo. Aunque es contagiosa, su transmisión exacta aún no se comprende completamente. Para cuidarse, es importante evitar el contacto cercano con personas enfermas y mantener una buena higiene.</Text>
                </View>
            </View>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 5,
        alignItems: 'center',
        backgroundColor: '#DEE1E6',
        marginTop: 35,
        marginLeft: 25,
        marginRight: 25,
        marginBottom: 35,
        borderRadius: 10
    },
    text: {
        marginTop: 25,
        color: 'black',
        fontSize: 20,
        textAlign: 'center',
        fontWeight: 'bold',
    },
    text: {
        marginLeft:7,
        marginTop: 25,
        color: 'black',
        fontSize: 18,
        textAlign: 'left',
        fontWeight: 'bold',
    },
    textInf: {
        marginLeft:7,
        marginRight: 7,
        marginTop: 10,
        color: 'black',
        fontSize: 15,
        textAlign: 'justify',
    },
    safeArea: {
        flex: 10,
        backgroundColor: 'white', // Cambia el color del fondo según el tema de tu app
    },
})

export default Register;
