import { StyleSheet } from "react-native";
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import ProfileCard from "./components/home/ProfileCard";
import { MaterialCommunityIcons } from "react-native-vector-icons"
import List from "./components/list/List";
import Name from "./components/name/Name"
import ChatGPT from "./components/chatgpt/ChatGPT";

const Tab = createBottomTabNavigator();
const [buttonColor, setButtonColor] = useState('gray');
const [textColor, setTextColor] = useState('gray');

const handleButtonClick = () => {
    // Cambiar el color del botón y el texto al hacer clic
    setButtonColor('green');
    setTextColor('green');
};


export default function Navigation() {
    return (
        <Tab.Navigator>
            <Tab.Screen name=" " style={[styles.button, { backgroundColor: buttonColor }]} component={ProfileCard} options={{

                tabBarLabel: "Diagnóstico",
                tabBarIcon: () => {
                    return <MaterialCommunityIcons onPress={handleButtonClick} name="hospital-box-outline" 
                    style={[styles.buttonText, { color: textColor }]} />
                }
            }} />
            <Tab.Screen name="list" component={List} options={{
                tabBarLabel: "Información",
                tabBarIcon: ({ color, size }) => {
                    return <MaterialCommunityIcons name="information" color={color} size={size} />
                }
            }} />
            <Tab.Screen name="name" component={Name} options={{
                tabBarLabel: "Estadísticas",
                tabBarIcon: ({ color, size }) => {
                    return <MaterialCommunityIcons name="chart-bar" color={color} size={size} />
                }
            }} />
            <Tab.Screen name="chatgpt" component={ChatGPT} options={{
                tabBarLabel: "Configuración",
                tabBarIcon: ({ color, size }) => {
                    return <MaterialCommunityIcons name="account-cog" color={color} size={size} />
                }
            }} />

        </Tab.Navigator>

    );
}

const styles = StyleSheet.create({
    button: {
        flexDirection: 'row',
        paddingVertical: 10,
        paddingHorizontal: 20,
        borderRadius: 5,
        alignItems: 'center',
      },
      buttonText: {
        marginLeft: 5,
        fontSize: 16,
      },
});
