import React, { useState } from "react";
import { Image, StyleSheet, View, Text, TextInput, TouchableOpacity, SafeAreaView } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import Sing_up from "./Sing_up";

const Login = () => {
    const logo = "https://i.postimg.cc/Zn9ScShf/logo.png";
    const [user, setUser] = useState('');
    const [password, setPassword] = useState('');
    const navigation = useNavigation();

    const handleSignIn = () => {
        // Redireccionar a la pantalla de navegación usando la función de navegación proporcionada por useNavigation
        navigation.navigate("Navigation");
    };

    const handleSignUp = () => {
        // Aquí redireccionas a la pantalla de registro cuando el usuario hace clic en el enlace.
        navigation.navigate("Sing_up");
    };

    return (
        <SafeAreaView style={styles.safeArea}>
            <View style={styles.container}>
                <View>
                    <Text style={styles.text}>BIENVENIDOS</Text>
                    <Image source={{ uri: logo }} style={styles.logo} />
                </View>
                <View>
                    <TextInput style={styles.user}
                        placeholder="Usuario"
                        value={user}
                        onChangeText={(text) => setUser(text)} />
                    <TextInput style={styles.password}
                        placeholder="Contraseña"
                        value={password}
                        onChangeText={(text) => setPassword(text)} />
                    <TouchableOpacity onPress={handleSignUp}>
                        <Text style={styles.signupLink}>Registrarse</Text>
                    </TouchableOpacity>
                </View>
                <TouchableOpacity style={styles.loginButton} onPress={handleSignIn}>
                    <Text style={styles.loginButtonText}>Iniciar</Text>
                </TouchableOpacity>

                <div>
            {buttonClicked ? (<Sing_up id={id}></Sing_up>): null}
            </div>
            </View>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 5,
        alignItems: 'center',
        backgroundColor: '#DEE1E6',
        marginTop: 35,
        marginLeft: 25,
        marginRight: 25,
        marginBottom: 35,
        borderRadius: 10
    },
    user: {
        color: "black",
        fontSize: 20,
        marginTop: -70,
        marginBottom: 20,
        padding: 10,
        borderWidth: 1,
        borderRadius: 3,
        borderColor: "#76768e",
        backgroundColor: "white",
    },
    password: {
        color: "black",
        fontSize: 20,
        marginTop: 10,
        marginBottom: 20,
        padding: 10,
        borderWidth: 1,
        borderRadius: 3,
        borderColor: "#76768e",
        backgroundColor: "white",
    },
    text: {
        marginTop: 25,
        color: 'black',
        fontSize: 20,
        textAlign: 'center',
        fontWeight: 'bold',
    },
    logo: {
        width: 200,
        height: "50%",
    },
    safeArea: {
        flex: 10,
        backgroundColor: 'white',
    },
    signupLink: {
        color: 'blue',
        textDecorationLine: 'underline',
        textAlign: 'center',
        marginBottom: 20,
    },
    loginButton: {
        backgroundColor: "#fd7858",
        borderRadius: 5,
        paddingVertical: 10,
        paddingHorizontal: 20,
        marginTop: 10,
    },
    loginButtonText: {
        color: 'white',
        fontSize: 18,
        textAlign: 'center',
    }
})

export default Login;
