import React, { useState } from "react";
import { Image, StyleSheet, View, Text, TextInput, SafeAreaView } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome'; // Importa el ícono de persona

const Login = () => {
  const logo = "https://i.postimg.cc/Zn9ScShf/logo.png";
  const [user, setUser] = useState('');
  const [password, setPassword] = useState('');

  return (
    <SafeAreaView style={styles.safeArea}>
      <View style={styles.container}>
        <View>
          <Text style={styles.text}>BIENVENIDOS</Text>
          <Image source={{ uri: logo }} style={styles.logo} />
        </View>
        <View>
          {/* Agrega el ícono de persona al input de Usuario */}
          <View style={styles.inputContainer}>
            <Icon name="user" size={20} color="black" style={styles.icon} />
            <TextInput
              style={styles.user}
              placeholder="Usuario"
              value={user}
              onChangeText={(text) => setUser(text)}
            />
          </View>

          {/* Agrega el ícono de persona al input de Contraseña */}
          <View style={styles.inputContainer}>
            <Icon name="user" size={20} color="black" style={styles.icon} />
            <TextInput
              style={styles.password}
              placeholder="Contraseña"
              value={password}
              onChangeText={(text) => setPassword(text)}
              secureTextEntry
            />
          </View>
        </View>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 5,
    alignItems: 'center',
    backgroundColor: '#DEE1E6',
    marginTop: 35,
    marginLeft: 25,
    marginRight: 25,
    marginBottom: 35,
    borderRadius: 10
  },
  inputContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
    borderWidth: 1,
    borderRadius: 3,
    borderColor: "#76768e",
    backgroundColor: "white",
    padding: 10,
  },
  user: {
    color: "black",
    fontSize: 20,
    flex: 1,
  },
  password: {
    color: "black",
    fontSize: 20,
    flex: 1,
  },
  text: {
    marginTop: 25,
    color: 'black',
    fontSize: 20,
    textAlign: 'center',
    fontWeight: 'bold',
  },
  logo: {
    width: 200,
    height: "50%", // Ajusta la altura deseada para la imagen
  },
  safeArea: {
    flex: 10,
    backgroundColor: 'white', // Cambia el color del fondo según el tema de tu app
  },
  icon: {
    marginRight: 10,
  },
});

export default Login;
