import React, { useState } from "react";
import { Image, StyleSheet, View, Text, TextInput, SafeAreaView } from 'react-native'

const Login = () => {
    const logo = "https://i.postimg.cc/Zn9ScShf/logo.png";
    const [user, setUser] = useState('');
    const [password, setPassword] = useState('');

    return (
        <SafeAreaView style={styles.safeArea}>
            <View style={styles.container}>
                <View>
                    <Text style={styles.text}>BIENVENIDOS</Text>
                    <Image source={{ uri: logo }} style={styles.logo} />
                </View>
                <View>
                    <TextInput style={styles.name}
                        placeholder="Usuario"
                        value={user}
                        onChangeText={(text) => setUser(text)} />
                    <TextInput style={styles.name}
                        placeholder="Contraseña"
                        value={password}
                        onChangeText={(text) => setPassword(text)} />
                </View>
            </View>
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 5,
        alignItems: 'center',
        backgroundColor: '#DEE1E6',
        marginTop: 35,
        marginLeft: 25,
        marginRight: 25,
        marginBottom: 35,
        borderRadius: 10
    },
    name: {
       
        fontSize: 15,
        marginTop: -70,
        marginLeft: 20,
        marginRight: 20,
        marginBottom: 20,
        padding:7,
        fontWeight: '400',
        paddingLeft: 10,
        borderWidth: 1,
        borderRadius: 3,
        borderColor: "black",
    },
    text: {
        marginTop: 25,
        color: 'black',
        fontSize: 20,
        textAlign: 'center',
        fontWeight: 'bold',
    },
    logo: {
        width: 200,
        height: "50%", // Ajusta la altura deseada para la imagen
    },
    safeArea: {
        flex: 10,
        backgroundColor: 'white', // Cambia el color del fondo según el tema de tu app
    },
})

export default Login;
