import React from 'react'
import { Image, StyleSheet, View, Text } from 'react-native'

const Information = () => {
    const sintomas = "https://i.postimg.cc/JzmV3y5G/c1743e96-c54b-4d72-b921-379ceb24771f.png";
    return (
        <View style={styles.container}>
            <View>
                <Text style={styles.title}>INFORMACIÓN ENFERMEDADES</Text>
                <Image source={{ uri: sintomas }} style={styles.sintomas} />
            </View>

            <View>
                <Text style={styles.text}>VARICELA</Text>
                <Text style={styles.textInf}>Es una enfermedad viral altamente contagiosa causada por el virus
                    varicela-zóster. Se caracteriza por la aparición de ampollas pruriginosas en todo el cuerpo. 
                    La transmisión ocurre a través del contacto directo con las lesiones de la piel o mediante 
                    el contacto con gotas respiratorias de una persona infectada. Para prevenir su propagación, 
                    es esencial mantener a los pacientes en cuarentena hasta que todas las ampollas hayan formado
                    costras y evitar el contacto cercano con personas no inmunes.</Text>

            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 5,
        alignItems: 'center',
        backgroundColor: '#DEE1E6',
        marginTop: 35,
        marginLeft: 25,
        marginRight: 25,
        marginBottom: 35,
        borderRadius:10
    },
    title:{
        marginTop: 25,
        color: 'black',
        fontSize: 20,
        textAlign: 'center',
        fontWeight: 'bold',        
    },
    text:{
        marginTop: 25,
        color: 'black',
        fontSize: 17,
        textAlign: 'left',
        fontWeight: 'bold',        
    },
    textInf:{
        marginTop: 10,
        color: 'black',
        fontSize: 15,
        textAlign: 'justify',      
    },
    sintomas: {
        marginTop: 30,
        width: 320,
        height: 50, // Ajusta la altura deseada para la imagen
    },
})

export default Information;
