import * as yup from "yup";
import {Formik} from "formik";
import {Dialog, Input, Switch} from '@rneui/themed';
import React, {useState} from "react";
import * as ImagePicker from 'expo-image-picker';
import axiosClient from "../../config/axiosConfig";
import {validateCedula} from "../../utils/validators";
import Icon from 'react-native-vector-icons/FontAwesome';
;

import {
    Button,
    Image,
    SafeAreaView,
    ScrollView,
    StyleSheet,
    Text,
    TextInput,
    TouchableOpacity,
    View
} from 'react-native'
const Register = () => {
    const analizar = <Icon name='check' size={20} color="white"/>;
    const [image, setImage] = useState(null)
    const [visible, setVisible] = useState(false)
    const [diagnostic, setDiagnostic] = useState(null)
    const [loading, setLoading] = useState(false)
    const cerrarIcon = <Icon name='image' size={100} color="#fd7858"/>;
    const pickImage = async () => {
        let result = await ImagePicker.launchImageLibraryAsync();
        if (!result.canceled) {
            setImage(result.assets[0]);
        }
    }

    const uriToBlob = (uri) => {
        return new Promise((resolve, reject) => {
            const xhr = new XMLHttpRequest();
            xhr.onload = function() {
                // return the blob
                resolve(xhr.response);
            };

            xhr.onerror = function() {
                // something went wrong
                reject(new Error('uriToBlob failed'));
            };
            // this helps us get a blob
            xhr.responseType = 'blob';
            xhr.open('GET', uri, true);

            xhr.send(null);
        });
    }
    const handleSignIn = async (values) => {
        if (image === null) {
            return
        }
        setDiagnostic(null)
        const data = new FormData();
        try {
            let blob=null
        // const blob=await uriToBlob(image.uri)
        //  console.log(blob)
        // const r = await fetch(image.uri);
        // console.log(r)
        // const blob = await r.blob();
        // data.append('image', blob, `image.jpg`)
            data.append('image',{
                uri:image.uri,
                name:image.fileName,
                fileName:image.fileName,
                type:image.type
            })
            console.log(data.get('image'))
        for (const key in values) {
            data.append(key, values[key])
        }
            setLoading(true)
            setVisible(true)
